  constructor() {
    super()

    this.state = {
      isLoading: true,
      temp: undefined,
      city: undefined,
      country: undefined,
      humidity: undefined,
      description: undefined
    }
  }
  getWeather = async (event) => {
    event.preventDefault()
    const city = event.target.elements.city.value
    const country = event.target.elements.country.value
    const reponse = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=London,uk&appid=${APPID}&units=metric`)
    const data = await reponse.json()

    this.setState({

      isLoading: false,
      temp: data.main.temp,
      city: data.name,
      country: data.sys.country,
      humidity: data.main.humidity,
      description: data.weather[0].description

    })

  }



render() {
  return(

<nav>
    <Link to="/Home"><h3>Home</h3></Link>
  <div>


       {!this.state.isLoading && <h1 style={{ marginTop: "70px" }}>City: {this.state.city}</h1>}
