import React,{Component} from 'react'
import { Link, withRouter } from 'react-router-dom'


const APPID = "93554df041fc50be24dc41455ad747c1"
class Nav extends Component {
  constructor() {
    super()

    this.state = {
      isLoading: true,
      temp: undefined,
      city: undefined,
      country: undefined,
      humidity: undefined,
      description: undefined
    }
  }
  getWeather = async (event) => {
    event.preventDefault()
    const city = event.target.elements.city.value
    const country = event.target.elements.country.value
    const reponse = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=london,uk&appid=${APPID}&units=metric`)
    const data = await reponse.json()

    this.setState({

      isLoading: false,
      temp: data.main.temp,
      city: data.name,
      country: data.sys.country,
      humidity: data.main.humidity,
      description: data.weather[0].description

    })

  }



render() {
  return(

<nav>
    <Link to="/Home"><h3>Home</h3></Link>
  <div>
  <div className="App">

       <nav className="navbar navbar-light bg-danger">
         <a className="navbar-brand" href="#">Harivatsav's Weather App ©</a>
       </nav>
       <div className="jumbotron">
       <h1 className="display-4">Hello Guys, Welcome to my weather app.</h1>
       <p className="lead">Come here to stay informed about the upcoming weather forecast.</p> 
   </div>

       {!this.state.isLoading && <h1 style={{ marginTop: "70px" }}>City: {this.state.city}</h1>}
       {!this.state.isLoading && <h1>Country: {this.state.country}</h1>}
       {!this.state.isLoading && <h3>Tempreture: {this.state.temp}℃</h3>}
       {!this.state.isLoading && <h3>Humidity: {this.state.humidity}</h3>}
       {!this.state.isLoading && <p>Description: {this.state.description}</p>}

     </div>
  </div>
</nav>




  );
 }
}

export default Nav
